import React, { useState } from "react";
import "./Test.css";

const Test = () => {
  const questions = [
    "질문 1",
    "질문 2",
    "질문 3",
    "질문 4",
    "질문 5",
    "질문 6",
    "질문 7",
    "질문 8",
    "질문 9",
    "질문 10",
    "질문 11",
    "질문 12",
  ];

  const choices = ["매우 그렇다", "그렇다", "보통", "아니다", "매우 아니다"];

  const [answers, setAnswers] = useState(Array(questions.length).fill(""));

  const handleButtonClick = (index, choice) => {
    let newAnswers = [...answers];
    newAnswers[index] = choice;
    setAnswers(newAnswers);
  };

  return (
    <div className="container">
      <h1>MBTI 검사</h1>
      {questions.map((question, index) => (
        <div key={index}>
          <h3>{question}</h3>
          {choices.map((choice, choiceIndex) => (
            <button
              key={choiceIndex}
              onClick={() => handleButtonClick(index, choice)}
              style={{
                backgroundColor: answers[index] === choice ? "blue" : "gray",
                marginRight: 10,
                marginBottom: 15,
              }}
            >
              {choice}
            </button>
          ))}
        </div>
      ))}
      <button onClick={() => console.log(answers)}>결과 제출</button>
    </div>
  );
};

export default Test;
