import React, { useState, useEffect } from "react";
import axios from "axios";
import { Modal } from "react-bootstrap";
import "./Questions.css";

const IPV4 = "143.248.195.86";

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await axios.get(`http://${IPV4}:4000/questions`);
      setQuestions(response.data);
    };

    fetchQuestions();
  }, []);

  const handleClose = () => setShowModal(false);
  const handleShow = (question) => {
    setSelectedQuestion(question);
    setShowModal(true);
  };

  return (
    <div className="container">
      <h1>질문 리스트</h1>
      {questions.map((question) => (
        <div
          key={question.id}
          className="question"
          onClick={() => handleShow(question)}
        >
          <h2>{question.title}</h2>
          <p>
            {new Intl.DateTimeFormat("en-GB", {
              year: "numeric",
              month: "2-digit",
              day: "2-digit",
              hour: "2-digit",
              minute: "2-digit",
            }).format(new Date(question.post_time))}{" "}
            by ${question.user_nickname}
          </p>
          ...
        </div>
      ))}
      {selectedQuestion && (
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{selectedQuestion.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <h4>{`Posted by ${selectedQuestion.user_nickname} at ${selectedQuestion.post_time}`}</h4>
            <p>{selectedQuestion.content}</p>
          </Modal.Body>
        </Modal>
      )}
    </div>
  );
};

export default Questions;
