import React, { useState, useEffect } from "react";
import axios from "axios";
import { Modal, Button } from "react-bootstrap";
import "./Questions.css";
import { format } from "date-fns";

const IPV4 = "143.248.195.86";

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [userId, setUserId] = useState(1); // replace this with the actual logged in user's id
  const [showModal, setShowModal] = useState(false);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await axios.get(`http://${IPV4}:4000/questions`);
      setQuestions(response.data);
    };

    fetchQuestions();
  }, []);

  useEffect(() => {
    const fetchVoteCounts = async () => {
      if (!selectedQuestion) return;

      const response = await axios.get(
        `http://${IPV4}:4000/questions/${selectedQuestion.id}/votes`
      );
      setVoteCounts(response.data);
    };

    fetchVoteCounts();
  }, [selectedQuestion]);

  const handleClose = () => setShowModal(false);
  const handleShow = (question) => {
    setSelectedQuestion(question);
    setShowModal(true);
  };

  const Bar = ({ yes, no }) => {
    const total = yes + no;
    const yesWidth = total > 0 ? (yes / total) * 100 : 0;
    const noWidth = total > 0 ? (no / total) * 100 : 0;

    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: 20,
          backgroundColor: "#ddd",
        }}
      >
        <div style={{ width: `${yesWidth}%`, backgroundColor: "green" }} />
        <div style={{ width: `${noWidth}%`, backgroundColor: "red" }} />
      </div>
    );
  };

  const vote = async (vote) => {
    try {
      await axios.post(`http://${IPV4}:4000/vote`, {
        question_id: selectedQuestion.id,
        user_id: userId,
        vote,
      });
      alert("Vote registered successfully");
    } catch (err) {
      alert(err.response.data.message);
    }
  };

  return (
    <div className="container">
      <h1>질문 리스트</h1>
      {questions.map((question) => (
        <div
          key={question.id}
          className="question"
          onClick={() => handleShow(question)}
        >
          <h2>{question.title}</h2>
          <p>
            {format(new Date(question.post_time), "yyyy-MM-dd HH:mm")} by{" "}
            {question.user_nickname}
          </p>
        </div>
      ))}
      {selectedQuestion && (
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{selectedQuestion.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              {`${format(
                new Date(selectedQuestion.post_time),
                "yyyy-MM-dd HH:mm"
              )} by ${selectedQuestion.user_nickname}`}
            </p>
            <p>{selectedQuestion.content}</p>
            <Button variant="primary" onClick={() => vote("Yes")}>
              Yes
            </Button>
            <Button variant="secondary" onClick={() => vote("No")}>
              No
            </Button>
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                flexWrap: "wrap",
              }}
            >
              <div style={{ flex: "0 0 50%" }}>
                <h4>E</h4>
                <Bar
                  yes={selectedQuestion.votes.E.yes}
                  no={selectedQuestion.votes.E.no}
                />
              </div>
              {/* repeat for other personality types */}
            </div>
          </Modal.Body>
        </Modal>
      )}
    </div>
  );
};

export default Questions;
