import React, { useState, useEffect } from "react";
import axios from "axios";
import { Modal } from "react-bootstrap";
import "./Questions.css";
import { format } from "date-fns";

const IPV4 = "143.248.195.86";

const Questions = () => {
  const [questions, setQuestions] = useState([]);
  const [userId, setUserId] = useState(1); // replace this with the actual logged in user's id
  const [showModal, setShowModal] = useState(false);
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  useEffect(() => {
    const fetchQuestions = async () => {
      const response = await axios.get(`http://${IPV4}:4000/questions`);
      setQuestions(response.data);
    };

    fetchQuestions();
  }, []);

  const handleClose = () => setShowModal(false);
  const handleShow = (question) => {
    setSelectedQuestion(question);
    setShowModal(true);
  };

  const vote = async (questionId, vote) => {
    try {
      await axios.post(`http://${IPV4}:4000/vote`, {
        question_id: questionId,
        user_id: userId,
        vote,
      });
      alert("Vote registered successfully");
    } catch (err) {
      alert(err.response.data.message);
    }
  };

  return (
    <div className="container">
      <h1>질문 리스트</h1>
      {questions.map((question) => (
        <div
          key={question.id}
          className="question"
          onClick={() => handleShow(question)}
        >
          <h2>{question.title}</h2>
          <p>
            {format(new Date(question.post_time), "yyyy-MM-dd HH:mm")} by{" "}
            {question.user_nickname}
          </p>
          <div>
            <button onClick={() => vote(question.id, "Yes")}>Yes</button>
            <button onClick={() => vote(question.id, "No")}>No</button>
          </div>
        </div>
      ))}
      {selectedQuestion && (
        <Modal show={showModal} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>{selectedQuestion.title}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <p>
              {`${format(
                new Date(selectedQuestion.post_time),
                "yyyy-MM-dd HH:mm"
              )} by ${selectedQuestion.user_nickname}`}
            </p>
            <p>{selectedQuestion.content}</p>
            <div>
              <button onClick={() => vote(selectedQuestion.id, "Yes")}>
                Yes
              </button>
              <button onClick={() => vote(selectedQuestion.id, "No")}>
                No
              </button>
            </div>
          </Modal.Body>
        </Modal>
      )}
    </div>
  );
};

export default Questions;
