import { useContext, useState, React } from "react";
import "./Test.css";
import { UserDataContext } from "./UserDataContext";

const Test = () => {
  const { userData } = useContext(UserDataContext);
  const questions = [
    "질문 1",
    "질문 2",
    "질문 3",
    "질문 4",
    "질문 5",
    "질문 6",
    "질문 7",
    "질문 8",
    "질문 9",
    "질문 10",
    "질문 11",
    "질문 12",
  ];

  const choices = ["매우 그렇다", "그렇다", "보통", "아니다", "매우 아니다"];

  const [answers, setAnswers] = useState(Array(questions.length).fill(""));

  const handleButtonClick = (index, choice) => {
    let newAnswers = [...answers];
    newAnswers[index] = choice;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    if (answers.includes("")) {
      alert("응답하지 않은 항목이 있습니다.");
    } else {
      console.log(answers);
      alert(userData.nickname);
    }
  };

  return (
    <div className="container">
      <h1 style={{ textAlign: "center" }}>MBTI 검사</h1>
      <div style={{ overflow: "auto", maxHeight: "80vh", textAlign: "center" }}>
        {questions.map((question, index) => (
          <div key={index}>
            <h3>{question}</h3>
            {choices.map((choice, choiceIndex) => (
              <button
                key={choiceIndex}
                onClick={() => handleButtonClick(index, choice)}
                style={{
                  backgroundColor:
                    answers[index] === choice ? "skyblue" : "#A4A4A4",
                  marginRight: 10,
                  marginBottom: 15,
                  borderRadius: 10,
                }}
              >
                {choice}
              </button>
            ))}
          </div>
        ))}
      </div>
      <button
        onClick={handleSubmit}
        style={{
          marginTop: 10,
          borderRadius: 10,
        }}
      >
        답변 완료
      </button>
    </div>
  );
};

export default Test;
