import { useContext, useState, React, useEffect, useCallback } from "react";
import "./Test.css";
import { UserDataContext } from "./UserDataContext";
import axios from "axios";
const IPV4 = "143.248.195.86";

const Test = () => {
  const { userData } = useContext(UserDataContext);
  const [userm1, setUserm1] = useState("m1");
  const [userm2, setUserm2] = useState("m2");
  const [userm3, setUserm3] = useState("m3");
  const [userm4, setUserm4] = useState("m4");
  const [testm1, setTestm1] = useState("tm1");
  const [testm2, setTestm2] = useState("tm2");
  const [testm3, setTestm3] = useState("tm3");
  const [testm4, setTestm4] = useState("tm4");
  const questions = [
    "질문 1",
    "질문 2",
    "질문 3",
    "질문 4",
    "질문 5",
    "질문 6",
    "질문 7",
    "질문 8",
    "질문 9",
    "질문 10",
    "질문 11",
    "질문 12",
  ];

  const choices = ["매우 그렇다", "그렇다", "보통", "아니다", "매우 아니다"];

  const [answers, setAnswers] = useState(Array(questions.length).fill(""));

  const handleButtonClick = (index, choice) => {
    let newAnswers = [...answers];
    newAnswers[index] = choice;
    setAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setTestm1("I");
    setTestm2("S");
    setTestm3("F");
    setTestm4("J");
    if (answers.includes("")) {
      alert("응답하지 않은 항목이 있습니다.");
    } else {
      alert(
        userData.nickname +
          "님의 원래 MBTI는 " +
          userm1 +
          userm2 +
          userm3 +
          userm4 +
          "이고, 검사 결과 나온 MBTI는 " +
          testm1 +
          testm2 +
          testm3 +
          testm4 +
          "입니다. 바꾸시겠습니까?"
      );
    }
  };

  const fetchUserMBTI = useCallback(async () => {
    try {
      const response = await axios.get(
        `http://${IPV4}:4000/user/${userData.nickname}`
      );
      setUserm1(response.data.m1);
      console.log(userm1);
      setUserm2(response.data.m2);
      console.log(userm2);
      setUserm3(response.data.m3);
      console.log(userm3);
      setUserm4(response.data.m4);
      console.log(userm4);
    } catch (err) {
      console.error(err);
    }
  }, [userData]);

  useEffect(() => {
    fetchUserMBTI();
  }, [fetchUserMBTI]);

  return (
    <div className="container">
      <h1 style={{ textAlign: "center" }}>MBTI 검사</h1>
      <div
        style={{
          overflow: "auto",
          maxHeight: "80vh",
          textAlign: "center",
          overflowY: "auto",
        }}
      >
        {questions.map((question, index) => (
          <div key={index}>
            <h3>{question}</h3>
            {choices.map((choice, choiceIndex) => (
              <button
                key={choiceIndex}
                onClick={() => handleButtonClick(index, choice)}
                style={{
                  backgroundColor:
                    answers[index] === choice ? "skyblue" : "#A4A4A4",
                  marginRight: 10,
                  marginBottom: 15,
                  borderRadius: 10,
                }}
              >
                {choice}
              </button>
            ))}
          </div>
        ))}
      </div>
      <button
        onClick={handleSubmit}
        style={{
          marginTop: 10,
          borderRadius: 10,
        }}
      >
        답변 완료
      </button>
    </div>
  );
};

export default Test;
